  1→"use client";
  2→
  3→import { useState, useEffect, DragEvent } from "react";
  4→
  5→interface Todo {
  6→  id: number;
  7→  text: string;
  8→  status: "backlog" | "todo" | "in-progress" | "done";
  9→  dueDate?: string;
 10→  priority?: "low" | "medium" | "high";
 11→  description?: string;
 12→}
 13→
 14→const STORAGE_KEY = "kanban-todos";
 15→
 16→const columns = [
 17→  { id: "backlog" as const, title: "Backlog", color: "bg-purple-500" },
 18→  { id: "todo" as const, title: "To Do", color: "bg-zinc-500" },
 19→  { id: "in-progress" as const, title: "In Progress", color: "bg-blue-500" },
 20→  { id: "done" as const, title: "Done", color: "bg-green-500" },
 21→];
 22→
 23→export default function Home() {
 24→  const [todos, setTodos] = useState<Todo[]>([]);
 25→  const [inputValue, setInputValue] = useState("");
 26→  const [isLoaded, setIsLoaded] = useState(false);
 27→  const [dueDate, setDueDate] = useState("");
 28→  const [draggedId, setDraggedId] = useState<number | null>(null);
 29→  const [editingId, setEditingId] = useState<number | null>(null);
 30→  const [editText, setEditText] = useState("");
 31→  const [priority, setPriority] = useState<"low" | "medium" | "high">("medium");
 32→  const [selectedTask, setSelectedTask] = useState<Todo | null>(null);
 33→
 34→  useEffect(() => {
 35→    const stored = localStorage.getItem(STORAGE_KEY);
 36→    if (stored) {
 37→      setTodos(JSON.parse(stored));
 38→    } else {
 39→      setTodos([
 40→        { id: 1, text: "Review pull request", status: "todo", dueDate: "2026-02-12", priority: "medium" },
 41→        { id: 2, text: "Fix login bug", status: "in-progress", dueDate: "2026-02-11", priority: "high" },
 42→        { id: 3, text: "Write unit tests", status: "todo", priority: "low" },
 43→        { id: 4, text: "Update documentation", status: "done", dueDate: "2026-02-08", priority: "low" },
 44→        { id: 5, text: "Deploy to staging", status: "in-progress", priority: "high" },
 45→        { id: 6, text: "Refactor auth module", status: "backlog", priority: "medium" },
 46→        { id: 7, text: "Set up CI/CD pipeline", status: "backlog", dueDate: "2026-02-20", priority: "high" },
 47→        { id: 8, text: "Design new landing page", status: "todo", dueDate: "2026-02-18", priority: "medium" },
 48→        { id: 9, text: "Fix mobile responsiveness", status: "in-progress", dueDate: "2026-02-14", priority: "medium" },
 49→        { id: 10, text: "Add dark mode toggle", status: "done", priority: "low" },
 50→        { id: 11, text: "Optimize database queries", status: "backlog", priority: "high" },
 51→        { id: 12, text: "Write API documentation", status: "todo", dueDate: "2026-02-16", priority: "low" },
 52→      ]);
 53→    }
 54→    setIsLoaded(true);
 55→  }, []);
 56→
 57→  useEffect(() => {
 58→    if (isLoaded) {
 59→      localStorage.setItem(STORAGE_KEY, JSON.stringify(todos));
 60→    }
 61→  }, [todos, isLoaded]);
 62→
 63→  // Close modal on Escape key
 64→  useEffect(() => {
 65→    const handleEscape = (e: KeyboardEvent) => {
 66→      if (e.key === "Escape" && selectedTask) {
 67→        setSelectedTask(null);
 68→      }
 69→    };
 70→    window.addEventListener("keydown", handleEscape);
 71→    return () => window.removeEventListener("keydown", handleEscape);
 72→  }, [selectedTask]);
 73→
 74→  const addTodo = () => {
 75→    if (inputValue.trim() === "") return;
 76→    setTodos([
 77→      ...todos,
 78→      {
 79→        id: Date.now(),
 80→        text: inputValue.trim(),
 81→        status: "todo",
 82→        dueDate: dueDate || undefined,
 83→        priority,
 84→      },
 85→    ]);
 86→    setInputValue("");
 87→    setDueDate("");
 88→    setPriority("medium");
 89→  };
 90→
 91→  const handleKeyDown = (e: React.KeyboardEvent) => {
 92→    if (e.key === "Enter") {
 93→      addTodo();
 94→    }
 95→  };
 96→
 97→  const deleteTodo = (id: number) => {
 98→    setTodos(todos.filter((todo) => todo.id !== id));
 99→  };
100→
101→  const handleDragStart = (e: DragEvent, id: number) => {
102→    setDraggedId(id);
103→    e.dataTransfer.effectAllowed = "move";
104→  };
105→
106→  const handleDragOver = (e: DragEvent) => {
107→    e.preventDefault();
108→    e.dataTransfer.dropEffect = "move";
109→  };
110→
111→  const handleDrop = (e: DragEvent, status: Todo["status"]) => {
112→    e.preventDefault();
113→    if (draggedId === null) return;
114→
115→    setTodos(
116→      todos.map((todo) =>
117→        todo.id === draggedId ? { ...todo, status } : todo
118→      )
119→    );
120→    setDraggedId(null);
121→  };
122→
123→  const handleDragEnd = () => {
124→    setDraggedId(null);
125→  };
126→
127→  const startEditing = (todo: Todo) => {
128→    setEditingId(todo.id);
129→    setEditText(todo.text);
130→  };
131→
132→  const saveEdit = () => {
133→    if (editingId === null) return;
134→    if (editText.trim() === "") {
135→      deleteTodo(editingId);
136→    } else {
137→      setTodos(
138→        todos.map((todo) =>
139→          todo.id === editingId ? { ...todo, text: editText.trim() } : todo
140→        )
141→      );
142→    }
143→    setEditingId(null);
144→    setEditText("");
145→  };
146→
147→  const handleEditKeyDown = (e: React.KeyboardEvent) => {
148→    if (e.key === "Enter") {
149→      saveEdit();
150→    } else if (e.key === "Escape") {
151→      setEditingId(null);
152→      setEditText("");
153→    }
154→  };
155→
156→  const isOverdue = (dueDate?: string) => {
157→    if (!dueDate) return false;
158→    const today = new Date();
159→    today.setHours(0, 0, 0, 0);
160→    return new Date(dueDate) < today;
161→  };
162→
163→  const formatDate = (dateStr: string) => {
164→    const date = new Date(dateStr + "T00:00:00");
165→    return date.toLocaleDateString(undefined, { month: "short", day: "numeric" });
166→  };
167→
168→  const getTodosByStatus = (status: Todo["status"]) => {
169→    return todos.filter((todo) => todo.status === status);
170→  };
171→
172→  const getPriorityColor = (priority?: "low" | "medium" | "high") => {
173→    switch (priority) {
174→      case "high":
175→        return "bg-red-500";
176→      case "medium":
177→        return "bg-yellow-500";
178→      case "low":
179→        return "bg-green-500";
180→      default:
181→        return "bg-zinc-400";
182→    }
183→  };
184→
185→  const updateTask = (updatedTask: Todo) => {
186→    setTodos(todos.map((t) => (t.id === updatedTask.id ? updatedTask : t)));
187→    setSelectedTask(updatedTask);
188→  };
189→
190→  const getStatusLabel = (status: Todo["status"]) => {
191→    const col = columns.find((c) => c.id === status);
192→    return col?.title || status;
193→  };
194→
195→  return (
196→    <div className="flex min-h-screen bg-zinc-50 font-sans dark:bg-zinc-900">
197→      {/* Left Sidebar */}
198→      <aside className="flex w-64 flex-shrink-0 flex-col border-r border-zinc-200 bg-white dark:border-zinc-700 dark:bg-zinc-800">
199→        <div className="p-4">
200→          <h2 className="text-lg font-bold text-zinc-900 dark:text-zinc-50">justdoit</h2>
201→        </div>
202→
203→        <nav className="flex-1 px-3 py-2">
204→          <ul className="space-y-1">
205→            <li>
206→              <a
207→                href="#"
208→                className="flex items-center gap-3 rounded-lg bg-zinc-100 px-3 py-2 text-sm font-medium text-zinc-900 dark:bg-zinc-700 dark:text-zinc-50"
209→              >
210→                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
211→                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
212→                </svg>
213→                All Tasks
214→                <span className="ml-auto rounded-full bg-zinc-200 px-2 py-0.5 text-xs dark:bg-zinc-600">
215→                  {todos.length}
216→                </span>
217→              </a>
218→            </li>
219→            <li>
220→              <a
221→                href="#"
222→                className="flex items-center gap-3 rounded-lg px-3 py-2 text-sm text-zinc-600 transition-colors hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-700"
223→              >
224→                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
225→                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
226→                </svg>
227→                In Progress
228→                <span className="ml-auto rounded-full bg-zinc-200 px-2 py-0.5 text-xs dark:bg-zinc-600">
229→                  {getTodosByStatus("in-progress").length}
230→                </span>
231→              </a>
232→            </li>
233→            <li>
234→              <a
235→                href="#"
236→                className="flex items-center gap-3 rounded-lg px-3 py-2 text-sm text-zinc-600 transition-colors hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-700"
237→              >
238→                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
239→                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
240→                </svg>
241→                Completed
242→                <span className="ml-auto rounded-full bg-zinc-200 px-2 py-0.5 text-xs dark:bg-zinc-600">
243→                  {getTodosByStatus("done").length}
244→                </span>
245→              </a>
246→            </li>
247→          </ul>
248→
249→          <div className="mt-6">
250→            <h3 className="px-3 text-xs font-semibold uppercase tracking-wider text-zinc-500 dark:text-zinc-400">
251→              Priority
252→            </h3>
253→            <ul className="mt-2 space-y-1">
254→              <li>
255→                <a
256→                  href="#"
257→                  className="flex items-center gap-3 rounded-lg px-3 py-2 text-sm text-zinc-600 transition-colors hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-700"
258→                >
259→                  <span className="h-2 w-2 rounded-full bg-red-500" />
260→                  High
261→                  <span className="ml-auto rounded-full bg-zinc-200 px-2 py-0.5 text-xs dark:bg-zinc-600">
262→                    {todos.filter((t) => t.priority === "high").length}
263→                  </span>
264→                </a>
265→              </li>
266→              <li>
267→                <a
268→                  href="#"
269→                  className="flex items-center gap-3 rounded-lg px-3 py-2 text-sm text-zinc-600 transition-colors hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-700"
270→                >
271→                  <span className="h-2 w-2 rounded-full bg-yellow-500" />
272→                  Medium
273→                  <span className="ml-auto rounded-full bg-zinc-200 px-2 py-0.5 text-xs dark:bg-zinc-600">
274→                    {todos.filter((t) => t.priority === "medium").length}
275→                  </span>
276→                </a>
277→              </li>
278→              <li>
279→                <a
280→                  href="#"
281→                  className="flex items-center gap-3 rounded-lg px-3 py-2 text-sm text-zinc-600 transition-colors hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-700"
282→                >
283→                  <span className="h-2 w-2 rounded-full bg-green-500" />
284→                  Low
285→                  <span className="ml-auto rounded-full bg-zinc-200 px-2 py-0.5 text-xs dark:bg-zinc-600">
286→                    {todos.filter((t) => t.priority === "low").length}
287→                  </span>
288→                </a>
289→              </li>
290→            </ul>
291→          </div>
292→        </nav>
293→
294→        <div className="border-t border-zinc-200 p-4 dark:border-zinc-700">
295→          <p className="text-xs text-zinc-500 dark:text-zinc-400">
296→            {todos.length} total tasks
297→          </p>
298→        </div>
299→      </aside>
300→
301→      {/* Main Content */}
302→      <div className="flex flex-1 flex-col py-8">
303→        <header className="px-6">
304→          <h1 className="mb-6 text-center text-3xl font-bold text-zinc-900 dark:text-zinc-50">
305→            justdoit
306→          </h1>
307→
308→          <div className="mx-auto mb-8 flex max-w-xl gap-2">
309→            <input
310→              type="text"
311→              value={inputValue}
312→              onChange={(e) => setInputValue(e.target.value)}
313→              onKeyDown={handleKeyDown}
314→              placeholder="Add a new task..."
315→              className="flex-1 rounded-lg border border-zinc-300 bg-white px-4 py-3 text-zinc-900 placeholder-zinc-400 focus:border-zinc-500 focus:outline-none dark:border-zinc-600 dark:bg-zinc-800 dark:text-zinc-50 dark:placeholder-zinc-500"
316→            />
317→            <input
318→              type="date"
319→              value={dueDate}
320→              onChange={(e) => setDueDate(e.target.value)}
321→              className="rounded-lg border border-zinc-300 bg-white px-3 py-3 text-zinc-900 focus:border-zinc-500 focus:outline-none dark:border-zinc-600 dark:bg-zinc-800 dark:text-zinc-50"
322→            />
323→            <select
324→              value={priority}
325→              onChange={(e) => setPriority(e.target.value as "low" | "medium" | "high")}
326→              className="rounded-lg border border-zinc-300 bg-white px-3 py-3 text-zinc-900 focus:border-zinc-500 focus:outline-none dark:border-zinc-600 dark:bg-zinc-800 dark:text-zinc-50"
327→            >
328→              <option value="low">Low</option>
329→              <option value="medium">Medium</option>
330→              <option value="high">High</option>
331→            </select>
332→            <button
333→              onClick={addTodo}
334→              className="rounded-lg bg-zinc-900 px-6 py-3 font-medium text-white transition-colors hover:bg-zinc-700 dark:bg-zinc-50 dark:text-zinc-900 dark:hover:bg-zinc-200"
335→            >
336→              Add
337→            </button>
338→          </div>
339→        </header>
340→
341→        <main className="flex flex-1 gap-4 overflow-x-auto px-6 pb-6">
342→        {columns.map((column) => (
343→          <div
344→            key={column.id}
345→            className="flex w-80 flex-shrink-0 flex-col rounded-xl bg-zinc-100 dark:bg-zinc-800"
346→            onDragOver={handleDragOver}
347→            onDrop={(e) => handleDrop(e, column.id)}
348→          >
349→            <div className="flex items-center gap-2 p-4">
350→              <div className={`h-3 w-3 rounded-full ${column.color}`} />
351→              <h2 className="font-semibold text-zinc-900 dark:text-zinc-50">
352→                {column.title}
353→              </h2>
354→              <span className="ml-auto rounded-full bg-zinc-200 px-2 py-0.5 text-sm text-zinc-600 dark:bg-zinc-700 dark:text-zinc-400">
355→                {getTodosByStatus(column.id).length}
356→              </span>
357→            </div>
358→
359→            <div className="flex-1 space-y-2 overflow-y-auto p-2">
360→              {getTodosByStatus(column.id).map((todo) => (
361→                <div
362→                  key={todo.id}
363→                  draggable
364→                  onDragStart={(e) => handleDragStart(e, todo.id)}
365→                  onDragEnd={handleDragEnd}
366→                  onClick={() => setSelectedTask(todo)}
367→                  className={`cursor-grab rounded-lg border border-zinc-200 bg-white p-3 shadow-sm transition-all hover:border-zinc-300 hover:shadow-md active:cursor-grabbing dark:border-zinc-700 dark:bg-zinc-900 dark:hover:border-zinc-600 ${
368→                    draggedId === todo.id ? "opacity-50" : ""
369→                  }`}
370→                >
371→                  <div className="flex items-start gap-2">
372→                    <div
373→                      className={`mt-1 h-2 w-2 flex-shrink-0 rounded-full ${getPriorityColor(todo.priority)}`}
374→                      title={`${todo.priority || "no"} priority`}
375→                    />
376→                    {editingId === todo.id ? (
377→                      <input
378→                        type="text"
379→                        value={editText}
380→                        onChange={(e) => setEditText(e.target.value)}
381→                        onKeyDown={handleEditKeyDown}
382→                        onBlur={saveEdit}
383→                        autoFocus
384→                        className="w-full rounded border border-zinc-300 bg-transparent px-2 py-1 text-zinc-900 focus:border-zinc-500 focus:outline-none dark:border-zinc-600 dark:text-zinc-50"
385→                      />
386→                    ) : (
387→                      <p
388→                        onDoubleClick={() => startEditing(todo)}
389→                        className={`text-sm ${
390→                          todo.status === "done"
391→                            ? "text-zinc-400 line-through dark:text-zinc-500"
392→                            : "text-zinc-900 dark:text-zinc-50"
393→                        }`}
394→                      >
395→                        {todo.text}
396→                      </p>
397→                    )}
398→                  </div>
399→
400→                  <div className="mt-2 flex items-center justify-between">
401→                    <div className="flex items-center gap-2">
402→                      {todo.dueDate && (
403→                        <span
404→                          className={`text-xs ${
405→                            todo.status === "done"
406→                              ? "text-zinc-400 dark:text-zinc-500"
407→                              : isOverdue(todo.dueDate)
408→                              ? "font-medium text-red-500"
409→                              : "text-zinc-500 dark:text-zinc-400"
410→                          }`}
411→                        >
412→                          {isOverdue(todo.dueDate) && todo.status !== "done"
413→                            ? "Overdue: "
414→                            : "Due: "}
415→                          {formatDate(todo.dueDate)}
416→                        </span>
417→                      )}
418→                      {todo.description && (
419→                        <span className="text-zinc-400 dark:text-zinc-500" title="Has notes">
420→                          <svg className="h-3.5 w-3.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
421→                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-3l-4 4z" />
422→                          </svg>
423→                        </span>
424→                      )}
425→                    </div>
426→                    <button
427→                      onClick={(e) => {
428→                        e.stopPropagation();
429→                        deleteTodo(todo.id);
430→                      }}
431→                      className="rounded px-2 py-1 text-xs text-zinc-400 transition-colors hover:bg-zinc-100 hover:text-red-500 dark:hover:bg-zinc-800 dark:hover:text-red-400"
432→                    >
433→                      Delete
434→                    </button>
435→                  </div>
436→                </div>
437→              ))}
438→
439→              {getTodosByStatus(column.id).length === 0 && (
440→                <p className="py-8 text-center text-sm text-zinc-400 dark:text-zinc-500">
441→                  Drop tasks here
442→                </p>
443→              )}
444→            </div>
445→          </div>
446→        ))}
447→      </main>
448→
449→        <footer className="px-6 text-center text-sm text-zinc-500 dark:text-zinc-400">
450→          {todos.length} total tasks · {getTodosByStatus("done").length} completed
451→        </footer>
452→      </div>
453→
454→      {/* Task Detail Modal */}
455→      {selectedTask && (
456→        <div
457→          className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 animate-in fade-in duration-150"
458→          onClick={() => setSelectedTask(null)}
459→        >
460→          <div
461→            className="mx-4 w-full max-w-lg rounded-xl bg-white p-6 shadow-xl dark:bg-zinc-800 animate-in zoom-in-95 slide-in-from-bottom-4 duration-200"
462→            onClick={(e) => e.stopPropagation()}
463→          >
464→            <div className="mb-4 flex items-center justify-between">
465→              <div className="flex items-center gap-3">
466→                <h2 className="text-lg font-semibold text-zinc-900 dark:text-zinc-50">
467→                  Task Details
468→                </h2>
469→                {selectedTask.dueDate && isOverdue(selectedTask.dueDate) && selectedTask.status !== "done" && (
470→                  <span className="rounded-full bg-red-100 px-2 py-0.5 text-xs font-medium text-red-600 dark:bg-red-900/30 dark:text-red-400">
471→                    Overdue
472→                  </span>
473→                )}
474→                {selectedTask.status === "done" && (
475→                  <span className="rounded-full bg-green-100 px-2 py-0.5 text-xs font-medium text-green-600 dark:bg-green-900/30 dark:text-green-400">
476→                    Completed
477→                  </span>
478→                )}
479→              </div>
480→              <button
481→                onClick={() => setSelectedTask(null)}
482→                className="rounded-lg p-2 text-zinc-400 transition-colors hover:bg-zinc-100 hover:text-zinc-600 dark:hover:bg-zinc-700 dark:hover:text-zinc-200"
483→                title="Close (Esc)"
484→              >
485→                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
486→                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
487→                </svg>
488→              </button>
489→            </div>
490→
491→            <div className="space-y-4">
492→              {/* Task Text */}
493→              <div>
494→                <label className="mb-1 block text-sm font-medium text-zinc-700 dark:text-zinc-300">
495→                  Task
496→                </label>
497→                <input
498→                  type="text"
499→                  value={selectedTask.text}
500→                  onChange={(e) => updateTask({ ...selectedTask, text: e.target.value })}
501→                  className="w-full rounded-lg border border-zinc-300 bg-white px-3 py-2 text-zinc-900 focus:border-zinc-500 focus:outline-none dark:border-zinc-600 dark:bg-zinc-700 dark:text-zinc-50"
502→                />
503→              </div>
504→
505→              {/* Status */}
506→              <div>
507→                <label className="mb-1 block text-sm font-medium text-zinc-700 dark:text-zinc-300">
508→                  Status
509→                </label>
510→                <select
511→                  value={selectedTask.status}
512→                  onChange={(e) => updateTask({ ...selectedTask, status: e.target.value as Todo["status"] })}
